function DeltaV = ImageMaker(Title, Bodys)  figure('visible', 'on');  title(Title);##  subplot(2,2, [1 3]);  axis('equal');  grid on;  hold on;  hs = [plot3(0,0,0,'o','color','b')];  lgd = {"Sun"};  for i = 1:length(Bodys)    hs(end+1) = plot3(Bodys(i).OrbitPosition(1,:),Bodys(i).OrbitPosition(2,:),Bodys(i).OrbitPosition(3,:),'tag',strjoin({Bodys(i).Tag, "OrbitPosition"},';'));    lgd(end+1) = strjoin({Bodys(i).Name, " orbit"});% e = ", num2str(Bodys(i).Excentricity), ", a = ", num2str(Bodys(i).SemiMajorAxis), " au"}, '');    hs(end+1) = plot3(Bodys(i).OrbitPerigee(1),Bodys(i).OrbitPerigee(2),Bodys(i).OrbitPerigee(3),'*','tag',strjoin({Bodys(i).Tag, "OrbitPerigee"},';'));    lgd(end+1) = strjoin({Bodys(i).Name, " perigee"});% V = ", num2str(Bodys(i).VelocityAtPerigee)," au/d"},'');    hs(end+1) = plot3(Bodys(i).OrbitApogee(1),Bodys(i).OrbitApogee(2),Bodys(i).OrbitApogee(3),'*','tag',strjoin({Bodys(i).Tag, "OrbitApogee"},';'));    lgd(end+1) = strjoin({Bodys(i).Name, " apogee"});% V = ", num2str(Bodys(i).VelocityAtApogee)," au/d"},'');    if strcmp(Bodys(i).Type, 'body')      hs(end+1) = plot3(Bodys(i).Position(1),Bodys(i).Position(2),Bodys(i).Position(3),'o','tag',strjoin({Bodys(i).Tag, "Position"},';'));      lgd(end+1) = strjoin({Bodys(i).Name});%, ", true anomaly = ", num2str(Bodys(i).TrueAnomaly), " deg V = ", num2str(Bodys(i).Velocity)," au/d"},'');    end  end  legend(hs, lgd);  hold off;end